mapper.insert:

INSERT
  INTO BOARD
       ( RECIPE_ID
       , EMAIL
       , CONTENT
       )
VALUES ( #{recipeId}
       , #{email}
       , #{content}
       )


INSERT
  INTO BOARD_IMG
       ( BOARD_ID
       , SORT_NO
       , IMG
       )
VALUES ( #{boardId}
       , #{sortNo}
       , #{img}
       )

mapper.update:

UPDATE BOARD
   SET CONTENT       = #{content}
     , MODIFIED_DATE = NOW()
 WHERE ID            = #{id}
   AND EMAIL         = #{email}

mapper.delete:

DELETE
  FROM BOARD
 WHERE ID    = #{id}
   AND email = #{email}

mapper.select:

SELECT COUNT(*)
  FROM BOARD_LIKE
 WHERE BOARD_ID = #{boardId}
   AND EMAIL    = #{email}


SELECT COUNT(*)
  FROM BOARD_LIKE
 WHERE BOARD_ID = #{boardId}

mapper.insert:

INSERT
  INTO BOARD_LIKE
       ( EMAIL
       , BOARD_ID
       , CREATE_DATE
       )
VALUES ( #{email}
       , #{boardId}
       , NOW()
       )

mapper.delete:

DELETE
  FROM BOARD_LIKE
 WHERE EMAIL    = #{email}
   AND BOARD_ID = #{boardId}
